import React, {useState} from 'react'
import {Grid,Paper,Avatar, TextField,FormControlLabel, Checkbox, Typography,Link} from '@mui/material'
import LockOutlinedIcon from '@mui/icons-material/LockOutlined'
import Button from '@mui/material/Button';





const Login = ()=>{

    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');

    const [errorPassword, setErrorPassword] = useState(false);
    const [errorPasswordText, setPasswordErrorText] = useState('');


    //Estilizacao
    const paperStyle={padding:20,height:'65vh',width:400,margin:'20px auto'}
    const avatarStyle={backgroundColor:'green',height:100,width:100}
    const iconStyle={height:70,width:70}
    const buttonStyle={height:50}


    const handleEmailChange = (e) => {
        setEmail(e.target.value);
    }

    const handlePasswordChange = (e) => {
        setPassword(e.target.value);
        if(password.length < 8){
            debugger;
            setErrorPassword(true);
            setPasswordErrorText('Senha fraca menos de 8 caracteres')
        }else{
            setErrorPassword(false);
            setPasswordErrorText('')
        }
    }

    return(
        <Grid>
            <Paper elevation={10} style={paperStyle}>
                <Grid align='center'marginTop={10}>
                    <Avatar style={avatarStyle}><LockOutlinedIcon style={iconStyle}></LockOutlinedIcon></Avatar>
                    <h3>Acesse sua conta</h3>
                </Grid>

                <Grid>
                    <TextField
                        label='Email'
                        placeholder='Email'
                        fullWidth required
                        value={email}
                        onChange={handleEmailChange}
                        error={false}>
                    </TextField>
                </Grid>
                <h1>{email}</h1>
                <Grid marginTop={4}>
                    <TextField
                        label='Senha'
                        placeholder='Senha'
                        fullWidth required 
                        type='password' 
                        value={password} 
                        onChange={handlePasswordChange} 
                        error={errorPassword}>
                    </TextField>
                    {errorPasswordText}
                </Grid>

                <Grid marginTop={2}>
                    <FormControlLabel
                    control={
                        <Checkbox name="checkedB" color='primary'></Checkbox>
                    } label="Lembrar senha">
                    </FormControlLabel>
                </Grid>
                <Grid marginTop={2}>
                    <Button variant="contained" type='submit' color='primary' fullWidth style={buttonStyle}>LOGAR</Button>
                </Grid>


                <Grid>
                    <Typography>
                        <Link href="#">
                            Esqueci minha senha
                        </Link>
                    </Typography>
                </Grid>
                <Grid marginTop={12}>
                    <Typography> Ainda não possui conta? 
                        <Link href="#">
                            Faça o cadastro!
                        </Link>
                    </Typography>
                </Grid>
            </Paper>
        </Grid>
    )
}

export default Login
