import React from 'react'
import MailLockIcon from '@mui/icons-material/MailLock';
import {Grid,Paper,Avatar,TextField,Button,Typography,Link} from '@mui/material'


function validarEmail(email) {
    var re = /\S+@\S+\.\S+/;
    return re.test(email);
}

const Recuperar = ()=>{


    const paperStyle={padding:20,height:'65vh',width:400,margin:'20px auto'}
    const avatarStyle={backgroundColor:'green',height:100,width:100}
    const iconStyle={height:70,width:70}
    const buttonStyle={height:50}


    return(
        <Grid>
            <Paper elevation={10} style={paperStyle}>
                <Grid align='center'marginTop={10}>
                    <Avatar style={avatarStyle}><MailLockIcon style={iconStyle}></MailLockIcon></Avatar>
                    <h2>Recuperar senha</h2>
                </Grid>
                <Grid>
                    <TextField
                        label='Email'
                        placeholder='Digite seu email'
                        fullWidth required> 
                    </TextField>
                </Grid>
                <Grid marginTop={4}>
                    <Button variant="contained" type='submit' color='primary' fullWidth style={buttonStyle}>Recuperar senha</Button>
                    <Typography>
                        <Link href="/login">
                            Voltar para tela de login
                        </Link>
                    </Typography>
                </Grid>
            </Paper>
        </Grid>
    );
}

export default Recuperar;
